class Solution {
    func subtractProductAndSum(_ n: Int) -> Int {
		//Split the integer into each individual digit in an array (converting to string, because that seems to be the best method in Swift right now)
        let digits = String(describing: n).compactMap { Int(String($0)) }

		//Get the sum, product and finally the difference
		let product = digits.reduce(1, *)
		let sum = digits.reduce(0, +)
        let ans = product - sum

        return ans
    }
}
