class Solution {
    fun subtractProductAndSum(n: Int): Int {
        val productOfDigits = n.productOfDigits()
        val productOfSums = n.sumOfDigits()
        return productOfDigits - productOfSums
    }

    fun Int.productOfDigits() : Int{
        return this.toString()
            .map { it.toString().toInt() }
            .reduce { product, element -> product * element }
    }

    fun Int.sumOfDigits() : Int{
        return this.toString()
            .map { it.toString().toInt() }
            .reduce { sum, element -> sum + element }
    }

}

