function averageWaitingTime(customers: number[][]): number {
    let startMakingOrder: number = customers[0][0];
    let totalWaitingTime: number = 0;
    for (let customer of customers){
        // let arrived: number = customer[0];
        // customer[0] customer arrived
        // customer[1] the waiting time for an order for the specific customer to complete
        if (customer[0] > startMakingOrder){
            startMakingOrder = customer[0] + customer[1];
        }else{
            startMakingOrder += customer[1];
        }
        totalWaitingTime += (startMakingOrder-customer[0]);
    }
    return totalWaitingTime/customers.length;
};
