class Solution {
    public double averageWaitingTime(int[][] customers) {
        int customerCount = customers.length;
        int[] waitTimes = new int[customerCount];
        int timeCurrent = 0;

        for (int i = 0; i < customerCount; i++) {
            int arrivalTime = customers[i][0];
            int orderPrepTime = customers[i][1];
            timeCurrent = Math.max(timeCurrent, arrivalTime);
            timeCurrent += orderPrepTime;
            waitTimes[i] = timeCurrent - arrivalTime;
        }

        double averageWaitTime = 0.0;
        for (int waitTime : waitTimes) {
            averageWaitTime += (double) waitTime;
        }
        averageWaitTime /= customerCount;
        return averageWaitTime;
    }
}
