        for (auto& customer: A) {
            auto [i, j] = tie(customer[0], customer[1]);
            time = max(i, last);
            last = time + j;
            wait.push_back(last - i);
        }
        return accumulate(wait.begin(), wait.end(), 0.0) / wait.size();
    }
};
