    def averageWaitingTime(self, customers):
        """
        :type customers: List[List[int]]
        :rtype: float
        """
        realTime=customers[0][0]
        total=0
        for ele in customers:
          if realTime==ele[0]:
            total+=ele[1]
            realTime+=ele[1]
          elif realTime>ele[0]:
