class Solution {
    public double averageWaitingTime(int[][] customers) {
        LinkedList<Map.Entry<Integer, Integer>> lastTime = new LinkedList<>();
        for (int i = 0; i < customers.length; i++) {
            if (lastTime.isEmpty()) {
                lastTime.add(Map.entry(customers[i][0] + customers[i][1], 0));
                var key = lastTime.get(i).getKey();
                lastTime.set(i, Map.entry(key, key - customers[i][0]));
            } else if (customers[i][0] > lastTime.peekLast().getKey()) {
                lastTime.add(Map.entry(customers[i][0] + customers[i][1], 0));
                var key = lastTime.get(i).getKey();
                lastTime.set(i, Map.entry(key, key - customers[i][0]));
            } else {
                lastTime.add(Map.entry(lastTime.peekLast().getKey() + customers[i][1], 0));
                var key = lastTime.get(i).getKey();
                lastTime.set(i, Map.entry(key, key - customers[i][0]));
            }
        }

        return lastTime.stream().map(x -> (double) x.getValue()).reduce(0.0, (x, y) -> x + y) / lastTime.size();
    }
}
