class Solution {
public:
    double averageWaitingTime(vector<vector<int>>& customers) {
        double totalWaiting = 0;
        double size = customers.size();
        int available = 1;
        for (auto& c : customers) {
            if (c[0] >= available) {
                totalWaiting += c[1];
                available = c[0] + c[1];
            } else {
                int finishTime = available + c[1];
                totalWaiting += finishTime - c[0];
                available = finishTime;
            }
        }
        return totalWaiting / size;
    }
};
