class Solution {
public:
    double averageWaitingTime(vector<vector<int>>& customers) {
        double wait_t = 0;
        int finish_t = 0;
        for(int i=0; i<customers.size(); i++) {
            int arr_t = customers[i][0];
            int start_t = arr_t >= finish_t ? arr_t : finish_t;
            finish_t = start_t + customers[i][1];
            wait_t += finish_t - arr_t;
        }
        return wait_t/customers.size();
    }
};
