object Solution {
    def averageWaitingTime(customers: Array[Array[Int]]): Double = {
        val (w, c, _) = customers.foldLeft((0L, 0L, 0L)) {
            case ((totalWait, totalCustomers, readyAt), a) =>
                val customerArrivesAt = a.head.toLong
                val timeToPrepare = a.last.toLong
                val startAt = Math.max(readyAt, customerArrivesAt)
                val nextReadyAt = startAt + timeToPrepare
                val customerWaits = nextReadyAt - customerArrivesAt
                (totalWait + customerWaits, totalCustomers + 1, nextReadyAt)
        }
        w.toDouble / c
    }
}
