        pending_execution_time = customers[0][1]

        for i in range(1, len(customers)):
            pending_execution_time = max(
                pending_execution_time - calculate_elapsed_time(customers[i][0], customers[i - 1][0]), 0) + \
                                     customers[i][1]
            sum_of_waiting_times += pending_execution_time

        return sum_of_waiting_times / len(customers)

