/**
 * @param {number[][]} customers
 * @return {number}
 */
var averageWaitingTime = function(customers) {
    let totalFinishTime = customers[0][0] + customers[0][1];
    let waitingTimeArr = [totalFinishTime - customers[0][0]];
    let waitingTime = 0;
    let arrivalTime = 0;
    let prepareTime = 0;
    for(let i=1; i<customers.length; i++)
    {
        arrivalTime = customers[i][0];
        prepareTime = customers[i][1]

        if(arrivalTime > totalFinishTime)
        {
            totalFinishTime = arrivalTime + prepareTime;
        }
        else
        {
            totalFinishTime += prepareTime;
        }
        waitingTime = totalFinishTime - arrivalTime;
        waitingTimeArr.push(waitingTime);
    }
    return (waitingTimeArr.reduce((a,b)=> a+b , 0) / waitingTimeArr.length);
};
```

