class Solution {
public:
    double averageWaitingTime(vector<vector<int>>& cou) {
        double ans = cou[0][1];
        int ltime = cou[0][0] + cou[0][1];
        for(int i=1;i<cou.size();i++){
            // we have to calculate for next meal to prepare
            ans += (cou[i][0] >= ltime ? 0 : ltime - cou[i][0]) + cou[i][1];
            if(ltime < cou[i][0]) ltime = cou[i][0];
            ltime += cou[i][1]; 
        }
        return (ans/cou.size());
    }
};
```

