public int maximumUnits(int[][] boxTypes, int truckSize) {
	Arrays.sort(boxTypes, Comparator.comparingInt(boxType -> -boxType[1]));
	var units = 0;

	for (var boxType : boxTypes) {
		units += Math.min(truckSize, boxType[0]) * boxType[1];
		truckSize -= boxType[0];

		if (truckSize <= 0)
			break;
	}

	return units;
}
