public class Solution {
    public int MaximumUnits(int[][] boxTypes, int truckSize) {
        var result = 0;

        // Sort the box types by number of units per box in descending order
        Array.Sort(boxTypes, (a, b) => {
           return b[1] - a[1]; 
        });

        // Determine how many units can be added to the truck
        var remainingSize = truckSize;
        foreach (var type in boxTypes) {
            // Get the box count and units per box
            var boxCount = type[0];
            var unitsPerBox = type[1];

            // Check to see if theres enough room to add more boxes
            var adjustedBoxCount = 0;            
            if (remainingSize - boxCount > 0) {
                adjustedBoxCount = boxCount; // All boxes can be added
            } else {
                adjustedBoxCount = remainingSize; // Only the remaining amount can be added
            }

            // Add the units to the result
            result += adjustedBoxCount * unitsPerBox;

            // Adjust remaining truck size with the added boxes
            remainingSize -= adjustedBoxCount;

            // Exit if there is no more room remaining
            if (remainingSize == 0) {
                break;
            }
        }                

        return result;
    }
}
