var maximumUnits = function(boxTypes, truckSize) {
    /*
    Sort the boxes by their unit size in descending order
    Keep taking all the boxes until we can
    If we can't take all the boxes then take the maximum boxes of the current box type which we can take. Exit the loop
    */
    let unitCount=0,boxesTaken;
    boxTypes.sort(function(a,b){return b[1]-a[1]});//Sort the boxes by their unit size in descending order
    for(let i=0;i<boxTypes.length;i++){
        if(truckSize-boxTypes[i][0]>=0){//Keep taking all the boxes until we can
            truckSize = truckSize-boxTypes[i][0];
            unitCount = unitCount+(boxTypes[i][0]*boxTypes[i][1]);
        }else{//If we can't take all the boxes then take the maximum boxes of the current box type which we can take. Exit the loop
            boxesTaken = truckSize;
            truckSize=truckSize-boxesTaken;//This will be zero
            unitCount = unitCount+(boxesTaken*boxTypes[i][1]);
            break;
        }
    }
    return unitCount;
};
