    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
        vector<pair<int, int>> v;
        for (int i = 0; i < boxTypes.size(); i++) {
            v.push_back({boxTypes[i][1], boxTypes[i][0]});
