        sort(v.rbegin(), v.rend());
        int ans = 0;
        for (int i = 0; i < boxTypes.size(); i++) {
            if (v[i].second <= truckSize) {
                ans += v[i].first * v[i].second;
                truckSize -= v[i].second;
            }
            else {
                ans += v[i].first * truckSize;
                truckSize = 0;
            }
        }
        return ans;
    }
};
