class Solution {
public:
    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
      int row = boxTypes.size();
      int ans = 0;
      int n = truckSize;
      int temp = INT_MIN;
      int unit = 0;


      for(int i = 0;i < row;i++){
          unit  = boxTypes[i][1];
          temp = boxTypes[i][0];
        for(int j = i+1; j < row;j++){
            if(unit < boxTypes[j][1]){
              swap(boxTypes[i][0],boxTypes[j][0]);
              swap(boxTypes[i][1],boxTypes[j][1]);
              unit  = boxTypes[i][1];
              temp = boxTypes[i][0];
            }
            else if(unit == boxTypes[j][1] ){
                if(temp > boxTypes[j][0]){
                    swap(boxTypes[i][0],boxTypes[j][0]);
                    swap(boxTypes[i][1],boxTypes[j][1]);
                    unit  = boxTypes[i][1];
                    temp = boxTypes[i][0];
                }
            }
        }
      }
      for(int i = 0; i < row;i++){
          if(boxTypes[i][0] >= n){
              ans += n*boxTypes[i][1];
              n = 0;
              break;
          }else{
               n = n - boxTypes[i][0];
              ans += boxTypes[i][0]*boxTypes[i][1];
          }
      }

      return ans;
    }
};
