class Solution {
public:
    //just compare here 
    static bool compare(const vector<int> &box1,const vector<int> &box2)
    {
        return box1[1]>box2[1];
    }
    //main function
    int maximumUnits(vector<vector<int>>& box, int truckSize) 
    {
        //get box size
        int n=box.size();
        //then sort it with comapring simulutanoeusly
        sort(box.begin(),box.end(),compare);
        //set total ='0'
        int total=0;
        int ans=0;
        for(int i=0;i<n;i++)
        {
            //check the condition if trucksize do not exceed with taken box units
            if(total+box[i][0]>truckSize)
            {
                ans+=(truckSize-total)*box[i][1];
                break;
            }
            else
            {
                //otherwise simply
                //do add the boxes with containing units
                ans+=box[i][0]*box[i][1];
                total+=box[i][0];
            }
        } 
        //print output
        return ans;
    }
};

