class Solution {
public:
    static bool comp(vector<int>&a, vector<int>&b){
        return a[1]>b[1] || (a[1]==b[1] && a[0]<b[0]);
    }
    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
        sort(boxTypes.begin(),boxTypes.end(),comp);
        int sum=0;
        for(auto i: boxTypes){
          if(truckSize-i[0]>=0){
              truckSize-=i[0];
              sum+=(i[0]*i[1]);
          }
          else{
              if(truckSize!=0 && i[0]>truckSize){
                  sum+=(truckSize*i[1]);
                  truckSize=0;
              }
          }
        }
        return sum;



}
};
```

