class Solution {
public:
    static bool compare(vector<int> a,vector<int> b){
        return a[1]>b[1];
    }

    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
        int ans=0;
        sort(boxTypes.begin(),boxTypes.end(),compare);
        int i=0;
        while(i<boxTypes.size() && truckSize>=0){
            int x=boxTypes[i][0];
            int y=boxTypes[i][1];
            if(truckSize>x){
                ans+=x*y;
                truckSize-=x;
            }
            else{
                ans+=truckSize*y;
                break;
            }
            i++;
        }
        return ans;
    }
};

