class Solution {
    public int maximumUnits(int[][] boxTypes, int truckSize) {
        Arrays.sort(boxTypes,(a,b) -> (b[1] - a[1])); //descending acc to units/box
        int count = 0;
        int idx = 0;
        int len = boxTypes.length;
        while(truckSize > 0 && idx < len){ //while there is space in truck
            int boxes = boxTypes[idx][0]; //boxes available of this type
            if(boxes <= truckSize){ // can use all the boxes
                count += boxTypes[idx][1] * boxes; //no.of units/box * no.of boxes
                truckSize -= boxes;
            }else{  // cannot use all the boxes availabe of this type
                count += boxTypes[idx][1] * truckSize;
                break; //as we don't have space in truck now
            }
            idx++;
        }

        return count;
    }
}
