```
class Solution {
public:
   static bool sortcomp(const vector<int>&a,const vector<int>&b)//This function sorts the 2d vector on the basis of the                                                                       second element in descending order
    {
        return a[1]>b[1];
    }
    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
        sort(boxTypes.begin(),boxTypes.end(),sortcomp);//Sort accepts 3 parameters

        long int total=0;
        long int sum=0;
        for(int i=0;i<boxTypes.size();i++)
        {
            sum+=boxTypes[i][0];//calculating the sum of the number of boxes
            if(sum>truckSize)//moment sum exceeds the truckSize
            {
                sum-=boxTypes[i][0];//subtract the last added sum
                total+=((truckSize-sum)*boxTypes[i][1]);//find some of the remaining boxes
                break;
            }
            else
            {
                total+=(boxTypes[i][0]*boxTypes[i][1]);//finding total units
            }
        }
        return total;
    }
};
```

