class Solution {

public:
    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
        int ans = 0;

        vector<pair<int, int>> data;

        for(auto i=0; i<boxTypes.size(); i++)
            data.push_back( make_pair(boxTypes[i][1], boxTypes[i][0]) );  // no of units per box , no of boxes

        sort(data.begin(), data.end());
        reverse(data.begin(), data.end());

        int i=0;

        while(truckSize != 0 && i<data.size()) {

            ans += min(truckSize, data[i].second) * data[i].first;
            truckSize -= min(truckSize, data[i].second);
            i++;

        }

        return ans;
    }
};
