class Solution {
public:
    int maximumUnits(vector<vector<int>>& boxTypes, int truckSize) {
        sort(boxTypes.begin(), boxTypes.end(), [&](auto& a, auto& b){return a[1] > b[1];}); // sort the input array descendingly
        int ans = 0;
        for(auto& e: boxTypes){
            if(truckSize < 0) break;
            int num_of_choosing_boxes = min(e[0], truckSize);
            ans += num_of_choosing_boxes * e[1];
            truckSize -=num_of_choosing_boxes;
        }
        return ans;
    }
};
