var maximumUnits = function(boxTypes, truckSize) {
    const bucket = [];
    let total = 0;

    for(let i = 0; i < boxTypes.length; i++) {
        const [numBox, numUnit] = boxTypes[i];
        if(!bucket[numUnit]) bucket[numUnit] = numBox;
        else bucket[numUnit] += numBox;
    }

    for(let i = bucket.length-1; i >= 0; i--) {
        if(!bucket[i]) continue;

        const numBox = bucket[i];

        if(truckSize >= numBox) {
            total += (numBox * i);
            truckSize -= numBox;
        } else {
            total += (truckSize * i);
            return total;
        }
    }
    return total;
};
```

