class Solution {
    public int countPairs(int[] deliciousness) {

        int mod = 1000000007;
        int count=0;
        HashMap<Integer, Integer> hm = new HashMap<>();

        for(int num : deliciousness)
        {
            int power = 1;
            for(int i=0; i<=21; i++)
            {
                if(hm.containsKey(power-num))
                {
                    count+=hm.get(power-num);
                    count%=mod;
                }
                power*=2;
            }
            hm.put(num,hm.getOrDefault(num,0)+1);
        }

        return count;
    }
}
