```csharp
public int CountPairs(int[] deliciousness)
{
	Dictionary<long, long> frequency = new Dictionary<long, long>();
	foreach (var num in deliciousness)
	{
		if (frequency.ContainsKey(num))
		{
			frequency[num]++;
		}

		else
		{
			frequency.Add(num, 1);
		}
	}

	long result = 0;
	const int MOD = (int)(1E9) + 7;
	// MaxSum =  2 ^ 20 + 2 ^ 20  = 2 ^ 21
	for (int i = 0; i <= 21; i++)
	{
		long nextPower = 1L << i;
		foreach (var key in frequency.Keys)
		{
			long other = nextPower - key;
			if (other >= 0 && frequency.ContainsKey(other))
			{
				if (key != other)
				{
					result = (result + (frequency[key] * frequency[other]));
				}

				else
				{
					result = (result + (frequency[key] * (frequency[key] - 1)));
				}
			}
		}
	}

	return (int)((result / 2) % MOD);
}
```

