class Solution {

    public int countPairs(int[] deli) {

        //generating powers of 2 upto 21 and storing in an array
        int[] squares = new int[22];
        squares[0]=1;
        for(int i=1;i<22;i++){
            squares[i] = squares[i-1]*2;
        }

        //HashMap for storing elements and their occurences
        Map<Integer,Integer> map = new HashMap<>();

        int len = deli.length;

        //count to return as result
        long count = 0;

        //loop from 0 to len-1
        for(int i=0; i<len; i++){
