            for(int j=0; j<=21; j++){

                int currTarget = squares[j];
                //currTarget-curr --> this is the number we need for the pair to be delicious
                if(map.containsKey(currTarget-curr)){
                    count += map.get(currTarget-curr);
                }
            }   
            //inc occurences / add new elements to hashmap
            if(map.containsKey(curr)){
               map.put(curr,map.get(curr)+1); 
            }
            else{
                map.put(curr,1);
            }
        }

        //returning mod 10e9+7
        return (int)(count % 1000000007);
    }

}


