        for num in good_pairs:
            for power in powers:
                if (power-num) in good_pairs:
                    if((power-num)!=num):
                        res1=res1+good_pairs[power-num]*good_pairs[num]
                    if((power-num)==num and good_pairs[num]!=1):
                        res2=res2+math.comb(good_pairs[num],2)
        return int((res1/2)+res2)%(10**9+7)
