class Solution {
    public int countPairs(int[] deliciousness) {
        long count = 0;
        int mod = 1000000007;
        Arrays.sort(deliciousness);
        Map<Integer, Long> map = new HashMap<>();
        for(int i : deliciousness){
            map.put(i, Long.valueOf(map.getOrDefault(i, Long.valueOf(0))) + 1);
        }
        List<Integer> list = new ArrayList(map.keySet());
        Collections.sort(list);
        for(int i = 0; i < list.size(); i++){
            int first = list.get(i);
            for(int j = 0; j <= 21; j++){
                if(map.containsKey((int)Math.pow(2, j) - first)){
                    int k = (int) Math.pow(2, j) - first;
                    if(k == first){
                        long n = helper(map.get(first));
                        count = (count + n) % mod;
                    }else if(k > first){
                        count = (count + map.get(first) * map.get(k)) % mod;
                    }
                }
            }
        }
        return (int) count;
    }
    private long helper(long n){
        long top = 1;
        long a = n;
        long b = n - 2;
        while(a > b){
            top *= a;
            a--;
        }
        return top / 2;
    }
}
