class Solution {
public:
    #define ll long 
    #define v vector
    #define mod 1000000007
    long power(long long x, long long y, long long p) //x^y
    {
        int res = 1; // Initialize result

        x = x % p; // Update x if it is more than or
                             // equal to p

        if (x == 0)
            return 0; // In case x is divisible by p;

        while (y > 0)
        {
            // If y is odd, multiply x with result
            if (y & 1)
                res = (res * x) % p;

            // y must be even now
            y = y >> 1; // y = y/2
            x = (x * x) % p;
        }
        return res;
    }

    int countPairs(vector<int>& d) {
        ll n=d.size();
        sort(d.begin(),d.end());
        int ans=0;
        for(ll i=0;i<=21;i++)
        {
            long long target=power(2,i,mod);
            ll start=0;
            ll end=n-1;

            while(start<end)
            {
                ll temp_ans=d[start]+d[end];

                if(temp_ans>target)
                {
                    end--;
                }
                else if(temp_ans<target)
                {
                    start++;
                }
                else 
