class Solution {
public:
    int countPairs(vector<int>& deliciousness) {
        unordered_map<long long,long long> mp;
        long long res = 0;
        for(int i=0;i<deliciousness.size();i++)
        {
            mp[deliciousness[i]]++;
        }
        for(auto it = mp.begin();it!=mp.end();it++)
        {
            for(int j=0;j<=21;j++)
            {
                int num = pow(2,j);
                int key = num - it->first;
                if(key>=it->first)
                {
                    if(key==it->first)
                    {
                        long long temp = mp[key]*(mp[key]-1);
                        res = (res + temp/2)%1000000007;
                    }
                    else if(mp.find(key)!=mp.end())
                    {
                        long long temp = mp[key]*it->second;
                        res = (res+temp)%1000000007;
                    }
                }
            }
        }
        return res;
    }
};
