        int mod = 1000000007;
        Map<Integer, Integer> map = new HashMap<>();    //map to store the sum
        for (int val : deliciousness){
            for (int j = 0;j <= 21;j++){ // we try to go up 2 power 21 (2 power 20 + 2 power 20)
                int sum = 1 << j;           //trying each of the power of 2
                if (sum >= val && map.containsKey(sum - val))  if the difference is there then compute the result
