private long sumPrevious(int start) {
			// If we have a set of 1s...  lets say 3,
			// then the unique combinations will be
			// count(3) => 2 + 1 = 3
			// count(4) => 3 + 2 + 1 = 6
			long sum = 0;
			for(int cnt = 0; cnt < start; cnt++)
				sum += cnt;
			return sum;
		}
		public int CountPairs(int[] deliciousness) {
			//long i = 0;
			//foreach(int b in deliciousness)
			//	i++;
			//Console.WriteLine("Length: " + i);


			// index numbers and count the occurances
			Dictionary<int, int> dicNumToOccurances = new Dictionary<int, int>();
			foreach(int num in deliciousness) {
				if(!dicNumToOccurances.ContainsKey(num))
					dicNumToOccurances.Add(num, 0);
				dicNumToOccurances[num]++;
			}

			// calculate all squares up to the max numbr seen
			HashSet<int> powersOfTwo = new HashSet<int>();
			for(int cnt = 0; cnt <= 21; cnt++)
				powersOfTwo.Add((int) Math.Pow(2, cnt));

			// get unique numbers
			int[] uniqueNumbers = new List<int>(dicNumToOccurances.Keys).ToArray();

			// for each unique number, compare to self and all others
			long count = 0;
			foreach(int currentNum in uniqueNumbers) {
				foreach(int powTwo in powersOfTwo) {
					int target = powTwo - currentNum;
					if(target == currentNum)
						count += sumPrevious(dicNumToOccurances[currentNum]);
					else if(dicNumToOccurances.ContainsKey(target) && currentNum < target)
						count += dicNumToOccurances[target] * dicNumToOccurances[currentNum];
				}
			}

			return (int) (count % 1000000007);
	}
