/**
 * @param {number[]} deliciousness
 * @return {number}
 */
var countPairs = function(foods) {
    const powers = []
    for (let i = 0; i <= 21; i++) {
        const power = 2 ** i
        powers.push(power)
    }


    // delness => count of delness
    const map = new Map()
    for (const food of foods.values()) {
        map.set(food, 1 + (map.get(food) || 0))
    }


    // count thru map
    const moduloMe = (10 ** 9) + 7
    let result = 0
    for (const [food, foodCount] of map.entries()) {
        for (let i = -1 + powers.length; i >= 0; i--) {
            const power = powers[i]
            // ensure that seekMe >= food
            if (power < food * 2)  break

            const seekMe = power - food
            const seekMeCount = map.get(seekMe) || 0

            let outcome = 0
            if (seekMe === food) {
                if (seekMeCount > 1) {
                    outcome = foodCount * (foodCount - 1) / 2
                }
            } else {
                if (seekMeCount > 0) {
                    outcome = foodCount * seekMeCount
                }
            }

            result += outcome
            result %= moduloMe
        }
    }


    return result
};
