class Solution {
public:
    int countPairs(vector<int>& deliciousness) {
        unordered_map<int, int> have;
        long long r = 0;
        for (int x : deliciousness) {
            for (int y = 1 << 21; y && y >= x; y >>= 1) {
                const auto t = have.find(y - x);
                if (t != have.end()) {
                    r += t->second;
                }
            }
            ++have[x];
        }
        return r % 1000000007;
    }
};
