from collections import Counter
class Solution:
    def countPairs(self, deliciousness: List[int]) -> int:
        c = Counter(deliciousness)
        possible_sum = [2**i for i in range(21, -1, -1)]
        result = 0
		# k is the number, v1 is the number of appearance in deliciousness
        for k, v1 in c.items():
            for p in possible_sum:
				# don't check for sums less than k *2, saves half os the checks
                if p < k * 2:
                    break
                v2 = c.get(p - k, 0)
				# if it is the same number, N_combination is v1 * (v1 - 1)
                if p - k == k:
                    result += v1 * (v1 - 1) // 2
				# otherwise, N_combination is just the product
                else:
                    result += v1 * v2
        return int(result % (1000000007))
```

