class Solution:
    def countPairs(self, deliciousness: List[int]) -> int:
        nums = {}
        max1, max2 = 0, 0

        for i in deliciousness:
            if i not in nums:
                nums[i] = 0
            nums[i] += 1

            if i > max1:
                max2, max1 = max1, i
            elif i > max2:
                max2 = i

        i, p = 1, pow(2, 0)
        # get the max sum of the biggest 2 numbers, no need to continue once that number has been reached
        _max = max1 + max2
        keys = list(nums.keys())
        count = 0

        # for each power of 2, check all key combinations that sum up to this power and add them
        while p <= _max:
            # keep track of the keys used, if we added (1-3), do not re-add (3-1)
            used_keys = set()
            for k in range(len(keys)):
                key1 = keys[k]
                key2 = p - key1
                if key2 in nums and key2 not in used_keys:
                    if key1 != key2:
                        # if the sum of these two numbers is a power of 2,
                        # add the product of their occurrences
                        count += nums[key1] * nums[key2]
                    elif nums[key1] > 1:
                        # adding nCr of this number (n! / ((n-r)! r!))
                        # ex: [1, 1, 1] can have 3 permutations
                        # ex: [1, 1, 1, 1] can have 6 permutations
                        count += factorial(nums[key1]) // (factorial((nums[key1] - 2)) * 2)

                used_keys.add(key1)
            p = pow(2, i)
            i += 1

        return count % (pow(10, 9) + 7)
```

