class Solution {
    private int MOD = 1_000_000_007;

    public int countPairs(int[] deliciousness) {
        Arrays.sort(deliciousness);
        Map<Integer, Integer> map = new HashMap<>();

        int count = 0;

        for (int d : deliciousness) {
            // Next power of two from current value
            int npot = (int)(Math.log(d)/Math.log(2)) + 1;
            int nextVal = 1 << npot;

            int diff = nextVal - d;

            if (map.containsKey(diff)) {
                count = (count + map.get(diff)) % MOD;
            } 

            // If d itself is power of two, then checking for whether 0 exist before
            if ((d & (d-1)) == 0 && map.containsKey(0) && d != 0) {
                count = (count + map.get(0)) % MOD;
            }

            map.put(d, map.getOrDefault(d, 0) + 1);
        }

        return count;
    }
}
