var countPairs = function(deliciousness) {
    let goodMeals = 0, map = new Map(), sqr;
    const MODULO = 10**9 + 7;

    for(let rate of deliciousness) {
        for(let i = 21; i >= 0; i--) {
            sqr = 2**i;
            if(sqr - rate < 0) break;
            if(map.has(sqr - rate)) { // Checking if sum make a power of 2
                goodMeals += map.get(sqr - rate); // if yes, then add the frequency
            }
        }
        map.set(rate, (map.get(rate) || 0) + 1);
    }
    return goodMeals % MODULO;
};
