const int mod = 1000000007;

int countPairs(vector<int>& ds) {
    int maxVal = 0;
    unordered_map<int, int> m;
    for (auto d: ds) {
        ++m[d];
        maxVal = max(maxVal, d);
    }

    long long result = 0;
    for (int power = 1; power <= 2 * maxVal; power = power << 1) {
        long long similar = 0;
        long long diff = 0;
        for (auto &p: m) {
            auto it = m.find(power - p.first);
            if (it != m.end()) {
                if (p.first == power - p.first) {
                    similar = (similar + static_cast<long long>(p.second) * (p.second - 1)/2) % mod;
                }
                else {
                    diff = (diff + static_cast<long long>(p.second) * it->second) % mod;
                }
            }
        }
        result = (result + similar + diff/2) % mod;
    }
    return static_cast<int>(result);
}

