class Solution {
    int mod = (int)1e9 +7;
    public int countPairs(int[] d) {
        Map<Integer, List<Integer>> hm = new HashMap<>();
        List<Integer> l;
        Arrays.sort(d);
        Map<Integer, Integer> h1 = new LinkedHashMap<>();
        for(int i = 0; i < d.length; i++) h1.put(d[i], h1.getOrDefault(d[i], 0) + 1);
        for(Map.Entry<Integer, Integer> m: h1.entrySet()) {
            l = new ArrayList<>();
            int k = 0, j = m.getKey(); 

            while(1<<k <= 1<<20) {
                if((1<<k) - j >= j){
                    l.add((1<<k) - j);
                    //System.out.print((1<<k) - j+" ");
                }
                k++;
            }   
            hm.put(m.getKey(), l);
        }
        //System.out.print("Tes");
        if(h1.size() == 1) {
            long p1 = 0;
            for(int i = 0; i <= 20; i++) {
                if(h1.containsKey(1<<i)) {
                    long q = h1.get(1<<i);
                    p1 = q*(q-1)/2;
                    //System.out.print((1<<i)+" "+ q);
                    return (int)(p1%mod);
                }
            }
            return (int)p1;
        }
        long res = 0, tot = 0, ans = 0;
        for(Map.Entry<Integer, Integer> m: h1.entrySet()) {
            l = hm.get(m.getKey());
            res = m.getValue();
            //System.out.print(m.getKey()+":"+m.getValue()+" ");
            tot = 0;
            for(int i = 0; i < l.size(); i++) {
                if(h1.containsKey(l.get(i))) {
                    long p = h1.get(l.get(i));
                    if((int)l.get(i) == (int)m.getKey()) {
                        if(res != 1) tot = res*(res-1)/2;
                    }else{
                        tot = (tot + (res*p))%mod;
                    }
                    // System.out.print(((int)l.get(i) == (int)m.getKey())+" "+ l.get(i)+ " "+ m.getKey());
                }

            }
            ans = (ans + tot)%mod;
        }
        return (int)ans%mod;

    }
}
