class Solution {
public:
    const int mod=1e9+7;
    int countPairs(vector<int>& arr) {
        unordered_map<long long int,long long int>mp;
        long long int ans=0;
        for(int i=0;i<arr.size();i++)
        {
            int ele=arr[i];
            long long int p=1;
            for(int j=0;j<=40;j++)
            {
                if(mp.find(p-ele)!=mp.end())
                {
                    ans=((ans%mod)+(mp[p-ele]%mod))%mod;
                }
                p=p*2;
            }
            mp[ele]++;
        }
        return ans%mod;
    }
};
