class Solution {
public:
    int mod = 1e9+7;
    int countPairs(vector<int>& nums) {
        int n = nums.size();
        map<int,int> m;
        for(auto &i:nums)
        m[i]++;
        int ans=0;
        for(int i=0;i<=21;i++)
        {
            int k = pow(2,i);
            for(auto &i:m)
            {
                int num1 = i.first;
                int num2 = k-num1;
                if(num2<num1)
                continue;
                if(m.find(num2)!=m.end())
                {
                    long long fre1 = m[num1];
                    long long fre2 = m[num2];
                    if(num1==num2)
                    {
                        if(m[num1]>1)
                        ans = (ans+(fre1*(fre1-1))/2)%mod;
                    }
                    else
                    {
                        ans = (ans+(fre1*fre2))%mod;
                    }
                }
            }
        }
        return ans;
    }
};
```

