    def countPairs(self, deliciousness: List[int]) -> int:
        tem={}
        for item in deliciousness:
            if(item in tem):
                tem[item]+=1
            else:
                tem[item]=1
        ans=0
        for item in sorted(tem):
            x=self.find(tem,item)
            ans+=x
            ans%=int(1e9+7)
        return ans
    def find(self,tem,val):
        ans=0
        for i in range(21,-1,-1):
            b=1<<i
            if((b-val)<val):
                break
            else:
                if(b-val in tem):
                    if(b-val!=val):
                        ans+=(tem[val]*tem[b-val])
                    else:
                        ans+=(tem[val]*(tem[val]-1))//2
                    ans%=(int(1e9+7))
        return ans
