```scala
def waysToSplit(nums: Array[Int]): Int = {

	val MOD = (1e9 + 7).toInt
	val N = nums.length

	// prefix array
	val A: Array[Int] = nums.map{var s = 0; d => {s += d; s}}

	def binarySearch(A: Array[Int], leftSum: Int, index: Int, searchLeft: Boolean): Int = {

		@tailrec
		def binarySearchHelper(l: Int, r: Int, acc: Int): Int = {
			if (l > r) acc
			else {
				val m = (r - l) / 2 + l
				val midSum = A(m) - A(index - 1)
				val rightSum = A(N - 1) - A(m)

				if (leftSum <= midSum && midSum <= rightSum)
					binarySearchHelper(if (searchLeft) l else m + 1, if (searchLeft) m - 1 else r, m)
				else if (leftSum > midSum) binarySearchHelper(m + 1, r, acc)
				else binarySearchHelper(l, m - 1, acc)
			}
		}

		binarySearchHelper(index, N - 2, -1)
	}

	@tailrec
	def waysToSplitHelper(i: Int, res: Int): Int = {
		if (i == N - 1) res
		else {
			val left = binarySearch(A, A(i - 1), i, searchLeft = true)
			val right = binarySearch(A, A(i - 1), i, searchLeft = false)
			waysToSplitHelper(i + 1, if (left == -1 || right == -1) res else (res + (right - left + 1)) % MOD)
		}
	}

	waysToSplitHelper(1, 0)
}
