class Solution {
public:
    int waysToSplit(vector<int>& nums) 
    {
        long long count=0,M=1000000007;
        int n=nums.size();

        vector<long long int> prefix(n,nums[0]);
        for(int i=1;i<n;i++)
        prefix[i]=prefix[i-1]+nums[i];
        // we have calculated the prefix sum for finding sum of lrm in O(1) time.

        for(int i=0;i<n-1;i++)
        {   
            int right=-1,left=-1; // for storing the indexes;

            int lo=i+1,hi=n-2;
            //we calculate the right-most valid index
            while(lo<=hi)
            {
                int mid=lo+(hi-lo)/2;
                if(prefix[mid]-prefix[i]<=prefix.back()-prefix[mid])
                {
                    right=mid;
                    lo=mid+1;
                }
                else
                    hi=mid-1;
            }

            lo=i+1;hi=n-2;
            //we calculate the left-most valid index
             while(lo<=hi)
            {
                int mid=lo+(hi-lo)/2;
                if(prefix[i]<=prefix[mid]-prefix[i])
                {
                    left=mid;
                    hi=mid-1;
                }
                else
                    lo=mid+1;
            }
            //invalid condition check i.e. if one of the bound does not exist 
            //or left bound is greater than right bound
            if(left==-1||right==-1||left>right)continue;

            count=(count+(right-left+1)%M)%M;
        }
        return count;
    }
};
